"""
.. mod:: overlaycontainer - Contains the emi-widget OverlayContainer.

.. synopsis::  Contains the emi-widget OverlayContainer.

.. automodule:: overlaycontainer

Contains the emi-widget OverlayContainer.

.. moduleauthor:: Alber Maximilian
"""
__all__ = [
    'OverlayContainer'
]

from tw.forms import FormField
from tw.api import CSSLink

from endian.emi.widgets.static import jquery_tools

DEFAULT_LINKSLOCATOR = ".overlaycontainer-target"
"""
The default element, which is taken to trigger the overlay.
Format: jQuery-locator
"""

class OverlayContainer(FormField):
    """
    An emi widget which creats an overlay, an displays
    inside html or an other widget.
    """

    template = "endian.emi.templates.overlaycontainer"
    engine_name = "mako"

    """
    The template of the widget.
    """
    
    css = [
        CSSLink(link='/toscawidgets/resources/endian.emi/static/css/overlaycontainer.css'),
        jquery_tools
    ]
    """
    The required js and css files of this module.
    """
    
    linksLocator = DEFAULT_LINKSLOCATOR
    """
    The element, which is taken to trigger the overlay.
    Format: jQuery-locator
    """
    
    content = ""
    """
    The content of the overlay. Can be either a string or
    a displayable widget.
    """

    params = ["linksLocator", "content"]

    def __init__(self,
                 linksLocator=DEFAULT_LINKSLOCATOR,
                 content="",
                 **params):
        """
        Initilaze the object.
        
        :param linksLocator: The HTML elemtn which should trigger
          the overlay.
        :type linksLocator: string, jQuery-Locator
        :param content: The content of the overlay.
        :type content: Either string or a displayable widget.
        """

        FormField.__init__(self, **params)
        self.linksLocator = linksLocator
        self.content = content
        pass

    pass
