from static import jquery_showpassword
from tw.forms import InputField, PasswordField as OriginalPasswordField
from tw.forms.validators import *

class BasePasswordField(OriginalPasswordField):
    
    params = {
        'size': 'The size of the text field',
        'min_length': 'The maximum size of the password',
        'max_length': 'The maximum size of the password',
        'nullable': 'If false, a value is required'
    }
    
    size = 41
    min_length = 0
    max_length = 1024
    validator = None
    engine_name = "mako"

    def __init__(self, *args, **kw):
        super(OriginalPasswordField, self).__init__(*args, **kw)
        if not self.validator:
            min_length = kw.get('min_length', 0)
            max_length = kw.get('max_length', 1024)
            nullable = kw.get('nullable', True)
            self.validator = String(not_empty=not nullable, min=min_length)
    
    def update_params(self, d):
        super(BasePasswordField, self).update_params(d)
        d.attrs['maxlength'] = self.max_length
        self.update_attrs(d, "size")

class PasswordField(BasePasswordField):
    template = "endian.emi.templates.password"
    engine_name = "mako"
    javascript = BasePasswordField.javascript + [jquery_showpassword]
    
    size = 30
    
__all__ = ['PasswordField', 'BasePasswordField']
