from textfield import TextField

class ProtectedTextField(TextField):

    params = {
        'size': 'The size of the text field',
        'maxlength': 'The maximum size of the field',
        'enable_button': 'Display a button to enable/disable the field',
        'enable_button_text': 'Text for the enable button'
    }
    
    template = "endian.emi.templates.protected_textfield"
    engine_name = "mako"

__all__ = [
    'ProtectedTextField',
]