import markupsafe
from tw.api import Link, CSSLink, JSLink
from tw.forms import FormField

import endian.core.i18n

class ReadonlyField(FormField):
    template = "endian.emi.templates.readonly"
    engine_name = "mako"
    label_text = ""
    markupsafe = False
    validator = None
    post = True # should a hidden input be added to be able to post this value
    
    expands = False
    params = [
        "expands",
        "format_function",
        "post"
    ]
    
    def update_params(self, d):
        super(ReadonlyField, self).update_params(d)
        # if value is a list join it with linebreaks
        if isinstance(d.value, list):
            tmp = []
            for value in d.value:
                if self.markupsafe:
                    value = markupsafe.escape(value)
                tmp.append(value)
            d.value = "<br />".join(tmp)
        elif self.markupsafe:
            d.value = markupsafe.escape(d.value)
        return d

__all__ = ['ReadonlyField']