from tw.forms import SingleSelectField as SingleSelectFieldBase
import endian.core.i18n
from endian.emi.widgets.static import jquery_chosen_css, jquery_chosen

__all__ = [
    'SingleSelectField',
    'SingleSelectFieldBase',
    'ChosenSingleSelectField',
]

# add lazy options support to singleselectfield
class SingleSelectField(SingleSelectFieldBase):

    engine_name = "mako"

    def update_params(self, d):
        if callable(d.get("options")):
            d["options"] = d["options"]()
        super(SingleSelectField, self).update_params(d)

class ChosenSingleSelectField(SingleSelectField):
    """
    User-friendly single select field usign the "Chosen" JavaScript plugin
    """
    engine_name = "mako"
    template = "endian.emi.templates.singleselect_chosen"
    css = [
        jquery_chosen_css,
    ]
    javascript = [
        jquery_chosen,
    ]
