from tw.forms import InputField

import endian.core.i18n
from endian.emi.widgets.static import *

__all__ = ["Slider"]

class Slider(InputField):
    template = "endian.emi.templates.slider"
    engine_name = "mako"
    css_classes = ["slider"]
    
    css = [
        jquery_ui_slider_css
    ]
    
    javascript = [
        jquery_ui_slider,
    ]
    type = "hidden"
    
    #config
    min_title = _("Minimum")
    max_title = _("Maximum")
    min_image = None#url
    max_image = None#url
    min = None
    max = None
    steps = None # list of tuples
    options = None # will be set if used in combination with target
    step_size = 1
    
    params = ["value_key",
              "description_key",
              "min_title",
              "max_title",
              "min_image",
              "max_image",
              "steps",
              "step_size"]
    
    def __init__(self, **params):
        # got a lazy list
        if self.options is not None:
            self.steps = self.options
        # create numerical steps from min to max
        if self.steps is None:
            self.steps = []
            if self.min is not None and \
                    self.max is not None:
                for i in range(self.min, self.max+1):
                    self.steps.append((i, "%s" % i))
        
        InputField.__init__(self, **params)
