from tw.api import\
    JSLink,\
    CSSLink
from static import jquery_tools
from container import\
    ChildContainer

__all__ = ['TabContainer']


class TabContainer(ChildContainer):
    css_classes=["tabcontainer"]
    css = [CSSLink(link='/toscawidgets/resources/endian.emi/static/css/tabcontainer.css'),
           jquery_tools]
    template = "endian.emi.templates.tabcontainer"
    engine_name = "mako"
    
    tabs = None
    panes_selector = None
    selected = 0

    params = ["tabs",
              "panes_selector",
              "selected"]

    def __init__(self,
                 id=None,
                 parent=None,
                 tabs=None,
                 panes_selector=None,
                 selected=None,
                 *args, **kws):
        if tabs is not None:
            self.tabs = tabs
        if panes_selector is not None:
            self.panes_selector = panes_selector
        if selected is not None:
            self.selected = selected
        ret = super(TabContainer, self).__init__(id=id,
                                                 parent=parent,
                                                 *args, **kws)
        if self.tabs == None:
            self.tabs = []
            for i, child in enumerate(self.children):
                if hasattr(child, 'id'):
                    self.tabs.append(child.id)
                else:
                    self.tabs.append(i)
        return ret

    def update_params(self, d):
        error = d.error
        super(TabContainer, self).update_params(d)
        d.error = error # we want error
