from tw.forms import TextArea as TwTextArea

class TextArea(TwTextArea):
   """
   @deprecated: TextArea has been deprecated: use MultilineTextField or HugeTextField instead.
   """
   def __init__(self, *args, **kargs):
       import warnings
       warnings.warn("TextArea has been deprecated: use MultilineTextField or HugeTextField instead.", 
                     DeprecationWarning, 2)
       TwTextArea.__init__(self, *args, **kargs)

class MultilineTextField(TwTextArea):
    """
    Multi line text area stored as a list of string
    """
    def update_params(self, d):
        super(MultilineTextField, self).update_params(d)
        if isinstance(d.value, list):
            d.value = "\n".join(d.value)

class HugeTextField(TwTextArea):
    """
    Multi line text area stored as a single string
    """
    pass

__all__ = [
    'TextArea',
    'MultilineTextField',
    'HugeTextField'
]