from tw.forms import TextField as TextFieldBase

class TextField(TextFieldBase):

    params = {
        'size': 'The size of the text field',
        'max_size': 'The maximum size of the field (DEPRECATED: use maxlength instead)',
        'maxlength': 'The maximum size of the field',
        'readonly_if_not_empty': 'If true, the field is editable only if the value is empty',
    }
    
    template = "endian.emi.templates.textfield"
    engine_name = "mako"

    def update_params(self,d):
        super(TextField, self).update_params(d)
        if d.get("readonly_if_not_empty") and d.get('value', None):
            d.attrs['readonly'] = True

__all__ = [
    'TextField',
    'TextFieldBase'
]