"""
.. mod:: uploadfield - Contains the emi-widget UpLoadField.

.. synopsis:: Contains the emi-widget UpLoadField.

.. automodule:: uploadfield

Contains the emi-widget UpLoadField

.. moduleauthor:: Alber Maximilian
"""

__all__ = [
    "UpLoadField"
]

from tw.forms import InputField
from tw.api import CSSLink

import endian.core.i18n

class UpLoadField(InputField):
    """
    Provides an upload section with title, description and upload form.
    """

    template = "endian.emi.templates.uploadfield"
    engine_name = "mako"
    
    css = [
        CSSLink(link='/toscawidgets/resources/endian.emi/static/css/uploadfield.css')
    ]
    """
    The required css files of this module.
    """

    title = ""
    """
    The title of the upload section.
    """
    
    description = ""
    """
    The description of the upload. Not used at the moment.
    """

    submit_title = _("Upload")
    """
    The title of the submit button.
    """

    download_link = None
    """
    A link to the previously uploaded file or None.
    """

    disabled = False
    
    params = ['title', 'submit_title', 'download_link', 'disabled']
    
    def __init__(self,
                 title=None,
                 description=None,
                 submit_title=None,
                 download_link=None,
                 disabled=False,
                 **params):
        """
        .. autofunction:: __init__

        Initilaze the object.

        :param title: The title of the upload section.
        :type title: String
        :param description: The description of the upload.
          Not used at the moment.
        :type description: String
        :param submit_title: The title of the submit button.
        :type submit_title: String
        :param download_link: A link to the previously uploaded
          file or None.
        :type download_link: None or String
        """
        InputField.__init__(self, **params)

        if title != None:
            self.title = title
        if description != None:
            self.description = description
        if submit_title != None:
            self.submit_title = submit_title
        if download_link != None:
            self.download_link = download_link
        self.disabled = disabled

    pass
