from tw.forms import InputField
from tw.api import CSSLink, JSLink

__all__ = [
    "WizardSummary"
]

class WizardSummary(InputField):
    template = "endian.emi.templates.wizard_summary"
    
    title = _("Configuration Summary")

    description = _("Here is a brief summary of what you configured")
    
    content = ""

    params = ["title", "description", "content"]

    def __init__(self, summary_fct=None, *args, **kw):
        self.summary_fct = summary_fct
        super(WizardSummary, self).__init__(*args, **kw)
        pass
    
    def update_params(self, d):
        super(WizardSummary, self).update_params(d)
        d["content"] = self.summary_fct()
        return d
    pass
