__date__ = "2011-11-08"

substRegistry = {
}

def registerSubst(name, method):
    """
        Register 'method' the method to be called in order to calculate
        the substitution value when prefix 'name' is found in a firewall
        config field.

        :param name: string within firewall config field which should trigger
                     this
        :type name: string
        :param method: pointer to the method which should be registered
        :type method: function
    """
    substRegistry[name] = method

def loadSubstitutions():
    """
        Loads all known substitutions
    """
    pass

__all__ = [
    'registerSubst',
    'loadSubstitutions',
    ]

