#!/usr/bin/python

__all__ = [
]

import os

from endian.migration.migration import mainHandler

CONFIG = "/var/efw/outgoing/config"

def main():
    if not os.path.exists(CONFIG):
        # nothing to migrate
        return
    config = open(CONFIG, "r")
    tmp = config.readlines()
    config.close()
    lines = []
    for line in tmp:
        line = line.rstrip()
        if line == "":
            continue
        while len(line.split(",")) < 12:
            line = "%s," % line
        lines.append(line)
    config = open(CONFIG, "w")
    config.write("\n".join(lines))
    config.close()

if __name__ == '__main__':
    mainHandler(main)