from endian.emi import controllers
from endian.emi.controllers.base import redirect
from endian.firewallgui.web import widgets

__all__ = ["FirewallController"]

class FirewallDiagramController(controllers.BaseController):

    def __init__(self):
        controllers.BaseController.__init__(self, containerWidget=widgets.DiagramGallery)

class FirewallController(controllers.Controller):
    diagram = FirewallDiagramController()
    
    @controllers.expose()
    def index(self, **args):
        redirect("diagram")

