__author__ = "Andrea Bonomi <a.bonomi@endian.com>"
__date__ = "2015-06-25"

from endian.data import DataSource
from endian.fw.substitutors import AbstractSubstitutor

__all__ = [
    'SubstZonesDev',
    'SubstZonesPrimaryIP',
]

class SubstZonesDev(AbstractSubstitutor):

    def process(self, value, rule=None, params=None):
        if value == 'ALL' or value == 'ANY' or value == '':
            return ''
        if value not in ['GREEN', 'BLUE', 'ORANGE', 'RED']:
            return value
        self.debug("Substitute ZONE '%s'" % value)
        ethernet_settings = DataSource('ethernet').settings
        if value != 'RED':
            return ethernet_settings["%s_DEV" % value]
        redifs = self.get_interface_by_uplink('ANY')
        if len(redifs) <= 0:
            self.debug("Substitution of ZONE '%s' failed. No red interface is up!" % value)
            raise Exception("No red interface is up")
        self.debug("Substituted ZONE '%s' to: %s" % (value, redifs))
        if self.list_separator:
            return self.list_separator.join(redifs)
        else:
            return redifs


class SubstZonesPrimaryIP(AbstractSubstitutor):

    def process(self, value, rule=None, params=None):
        if value == 'ALL' or value == 'ANY' or value == '':
            return ''
        if value not in ['GREEN', 'BLUE', 'ORANGE', 'RED']:
            return value
        self.debug("Substitute ZONE '%s'", value)
        ethernet_settings = DataSource('ethernet').settings
        if value != 'RED':
            ip = ethernet_settings.get("%s_ADDRESS" % value, "")
            if ip == '':
                return None
            self.debug("Substituted ZONE '%s' to: '%s'", value, ip)
            return ip
        return None

