# Add one more field to the CSV config file

import csv
import shutil
import os
import glob
from endian.migration.migration import mainHandler

CONFIG = "/var/efw/vpn/config"

def forcemove(f, to):
    for fff in glob.glob(f):
        moveto = to
        if os.path.isdir(to):
            basename = os.path.basename(fff)
            moveto = os.path.join(to, basename)
        try:
            shutil.move(fff, moveto)
        except:
            pass

def main():
    csv_in_file = None
    csv_out_file = None
    if not os.path.exists(CONFIG):
        return
    try:
        csv_in_file = open(CONFIG, 'r')
        csv_out_file = open(CONFIG + ".tmp", 'w+')
        reader = csv.reader(csv_in_file, delimiter=',')
        writer = csv.writer(csv_out_file, delimiter=',', quoting=csv.QUOTE_MINIMAL)
        for row in reader:
            old_length = len(row)
            if old_length < 2: # ignore empty line or line with only one item
                continue
            row = row + [""] * (33-len(row))
            if old_length < 33:
                row[32] = (row[4] == 'net') and 'push' or 'pull'
            row[20] = "|".join([x == 'sha' and 'sha1' or x \
                       for x in (row[20].split('|') or [])])
            writer.writerow(row)
    finally:
        if csv_in_file is not None:
            csv_in_file.close()
        if csv_out_file is not None:
            csv_out_file.close()
    shutil.move(CONFIG + ".tmp", CONFIG)
    # Restart ipsec
    from endian.job.engine_control import send_cmd_to_engine
    send_cmd_to_engine("restart ipsec --force")

if __name__ == '__main__':
    mainHandler(main)

