"""
.. mod:: clamavcron - Contains the ClamavCronJob job.

.. synopsis:: Contains the ClamavCronJob job.

.. automodule:: clamavcron

Contains the ClamavCronJob.

.. moduleauthor:: Peter Warasin
"""

from endian.job.commons import *
from endian.job.generic.cronjob import AnaCronJob

__all__ = [
    "ClamavCronJob"
]

class ClamavCronJob(AnaCronJob, Job):
    """
    .. autoclass:: ClamavCronJob

    Resets the Clamav cron scripts.
    """

    settings_file_name = "clamav/settings"
    scripts = {"freshclam": ("/usr/lib/cron/freshclam.cron", "UPDATE_SCHEDULE")}
    
    def pre_set(self, scripts):
        """
        set scripts to empty dict if clamav is not used by any service
        """
        if to_bool(call_job_function("clamav.shouldstart")):
            return scripts

        self.debug("Clamav AntiVirus cron job should not start.")
        return {}
    
    @action(name="start", 
            on="startup",
            depends=["system_accessible=start"],
            priority=30,
            exit_status="stop", 
            options={"debug":(bool,"Be more verbose."),
                     "no_unset":(bool,"Do not unset symlinks."),
                     "no_set":(bool,"Do not set symlinks."),
                     "no_pre_unset":(bool,"Do not trigger pre_unset hook."),
                     "no_post_unset":(bool,"Do not trigger post_unset hook."),
                     "no_pre_set":(bool,"Do not trigger pre_set hook."),
                     "no_post_set":(bool,"Do not trigger post_set hook.")}
            )
    def start(self, options):
        """
        .. autofunction:: start

        Resets the scripts.
        """
        AnaCronJob.start(self, options)
