from endian.job.commons import Job, action, EXIT_STATUS_START, EXIT_STATUS_STOP, monit, reload_service

class CollectdJob(Job):

    @action(name="start",
            on="startup",
            depends="emi=start",
            priority=40,
            options={"debug":(bool,"Be more verbose.")}
            )
    def start(self, options):
        Job.start(self, options)
        self.info("Starting collectd")
        monit("start collectd", logger=self.logger)
        return EXIT_STATUS_START

    @action(name="stop",
            options={"debug":(bool,"Be more verbose.")}
            )
    def stop(self, options):
        Job.stop(self, options)
        self.info("Stopping collectd")
        monit("stop collectd", logger=self.logger)
        return EXIT_STATUS_STOP

    @action(name="restart",
            depends="emi=start",
            options={"debug":(bool,"Be more verbose.")}
            )
    def restart(self, options):
        Job.restart(self, options)
        self.info("Restarting collectd")
        reload_service("collectd",
                       logger=self.logger,
                       changed=True,
                       can_reload=False)
        return EXIT_STATUS_START

    @action(name="updatewizard",
            on="updatewizard",
            priority=40,
            depends="emi=start",
            exit_status="start",
            options={"debug":(bool,"Be more verbose.")}
            )
    def updatewizard(self, options):
        self.info("Updatewizard collectd")
        return self.start(options)
