import os
from endian.data import DataSource
from endian.job.commons import Job, action, run, DONT_CHANGE_STATUS

LANGUAGES_FONTS = {'el': 'iso07u-16',
                   'pt': 'lat1-16',
                   'bz': 'lat1-16',
                   'cs': 'lat2-16',
                   'hu': 'lat2-16',
                   'pl': 'lat2-16',
                   'tr': 'lat5-16',
                   'vi': 'viscii10-8x16',
}

DEFAULT_FONT = 'lat0-16'

class ConsoleJob(Job):

    @action(name="start",
            on="startup",
            depends="modules",
            options={"debug":(bool,"Be more verbose.")}
            )
    def start(self, options):
        Job.start(self, options)
        try:
            settings = DataSource('main').settings
            keymap = settings.get('KEYMAP', "")
            language = settings.get('LANGUAGE', "")
        except AttributeError:
            keymap = ""
            language = ""

        if keymap:
            self.info("Setting keymap")
            run("loadkeys %s" % keymap, suppress_output=True)

        self.info("Setting locale")
        os.environ['LANG'] = 'en_US.utf8'

        self.info("Setting consolefonts")
        for i in range(2, 7):
            try:
                f = open("/dev/tty%s" % i, 'w')
                f.write("")
                f.close()
            except:
                pass

        font = LANGUAGES_FONTS.get(language, DEFAULT_FONT)
        run("unicode_start %s" % font, suppress_output=True)

    @action(name="reload",
            on="network_configured",
            exit_status=DONT_CHANGE_STATUS,
            )
    def reload(self, options):
        Job.start(self, options)
        # Send a SIGUSR1 signal to the efw-console(s)
        # When a console receives this signal, it redisplays the menu (with the updated ip address)
        run("killall -SIGUSR1 efw-console", suppress_output=True)
