from endian.job.commons import (
        Job,
        action,
        reload_service,
        monit,
        EXIT_STATUS_START,
        EXIT_STATUS_STOP,
        DONT_CHANGE_STATUS )

class FcronJob(Job):

    @action(name="start",
            on="startup",
            depends="emi=start",
            priority=99,
            options={"debug":(bool,"Be more verbose.")}
            )
    def start(self, options):
        Job.start(self, options)
        options['force'] = True
        return self.restart(options)

    @action(name="restart",
            exit_status="start",
            depends="emi=start",
            options={"debug":(bool,"Be more verbose.")}
            )
    def restart(self, options):
        Job.restart(self, options)
        self.info("Restarting fcron")
        reload_service("fcron",
                       logger=self.logger,
                       changed=True,
                       can_reload=False)
        return EXIT_STATUS_START


    @action(name="updatewizard",
            on="updatewizard",
            priority=40,
            exit_status=DONT_CHANGE_STATUS,
            options={"debug":(bool,"Be more verbose.")}
            )
    def updatewizard(self, options):
        self.info("Updatewizard fcron")
        return self.restart(options)


    @action(name="stop",
            options={"debug":(bool,"Be more verbose.")}
            )
    def stop(self, options):
        Job.stop(self, options)
        if options.get("onboot"):
            return EXIT_STATUS_STOP
        self.info("Stopping fcron")
        monit("stop fcron", logger=self.logger)
        return EXIT_STATUS_STOP

