from endian.job.commons import Job, action, led_ok

class SystemAccessibleJob(Job):

    @action(name="start",
            on="startup",
            priority=1,
            depends=["httpd=start", "network=start"],
            options={"debug":(bool,"Be more verbose.")}
            )
    def start(self, options):
        Job.start(self, options)
        self.info("System is accessible")
        led_ok()

    @action(name="first_startup",
            on="first_startup",
            exit_status="start",
            priority=1,
            depends=["cleanup=start", "hwclock=start", "syslogjob=start",
                     "monit=start", "console=start", "httpd=start", "network=start", "ssh=start"],
            options={"debug":(bool,"Be more verbose.")}
            )
    def first_startup(self, options):
        led_ok()

