import os
from endian.job.commons import Job, action, run

class UpdateWizardDoneJob(Job):

    @action(name="start",
            on="updatewizard",
            priority=100,
            options={"debug":(bool,"Be more verbose.")}
            )
    def start(self, options):
        Job.start(self, options)

        # Start a hook script '/var/efw/inithooks/updatewizard.local'
        if os.path.exists("/var/efw/inithooks/updatewizard.local"):
            run("/var/efw/inithooks/updatewizard.local")
